import React, {useState} from "react";
import Button from '@mui/material/Button';

function Calculator(){
const [number, setNumber]= useState(0);

function handleNumbers(event){
const buttonValue= event.target.value;
console.log(buttonValue);
setNumber((prevValue)=>{
    return prevValue*10+buttonValue;
});
}

function handleOperator(event){
const operatorValue = event.target.value;
console.log(operatorValue);
// if (operatorValue==="+") {
//     return number+buttonValue;
// }
}

    return (
        // <div class="container my-4">  

        <div class="calculator card">

    <input type="text" class="calculator-screen z-depth-1" value={number} disabled />

    <div class="calculator-keys">

      <Button variant="text" onClick={handleOperator} type="button" class="operator btn btn-info" value="+">+</Button>
      <Button variant="text" onClick={handleOperator} type="button" class="operator btn btn-info" value="-">-</Button>
      <Button variant="text" onClick={handleOperator} type="button" class="operator btn btn-info" value="*">&times;</Button>
      <Button variant="text" onClick={handleOperator} type="button" class="operator btn btn-info" value="/">&divide;</Button>

      <Button variant="text" onClick={handleNumbers} type="button" value="7" class="btn btn-light waves-effect">7</Button>
      <Button variant="text" onClick={handleNumbers} type="button" value="8" class="btn btn-light waves-effect">8</Button>
      <Button variant="text" onClick={handleNumbers} type="button" value="9" class="btn btn-light waves-effect">9</Button>


      <Button variant="text" onClick={handleNumbers} type="button" value="4" class="btn btn-light waves-effect">4</Button>
      <Button variant="text" onClick={handleNumbers} type="button" value="5" class="btn btn-light waves-effect">5</Button>
      <Button variant="text" onClick={handleNumbers} type="button" value="6" class="btn btn-light waves-effect">6</Button>


      <Button variant="text" onClick={handleNumbers} type="button" value="1" class="btn btn-light waves-effect">1</Button>
      <Button variant="text" onClick={handleNumbers} type="button" value="2" class="btn btn-light waves-effect">2</Button>
      <Button variant="text" onClick={handleNumbers} type="button" value="3" class="btn btn-light waves-effect">3</Button>


      <Button variant="text" onClick={handleNumbers} type="button" value="0" class="btn btn-light waves-effect">0</Button>
      <Button variant="text" onClick={handleNumbers} type="button" class="decimal function btn btn-secondary" value=".">.</Button>
      <Button variant="text" type="button" class="all-clear function btn btn-danger btn-sm" value="all-clear">AC</Button>

      <Button variant="text" type="button" class="equal-sign operator btn btn-default" value="=">=</Button>

    </div>
    </div>
    // </div>
    );
}

export default Calculator;
